class Solution {
public:
    int partition(vector<int> &A, int s, int e)
    {
        int pivot = A[s];

        int i = s;
        int j = e + 1;
        while(i < j)
        {
            while(i<e && A[i+1] <= pivot) i++;
            while(j > s && A[j] > pivot) j--;

            if(i<j)
                swap(A[i+1], A[j]);
        }
        swap(A[s], A[i]);
        return i;
    }
    int partition2(vector<int> &A, int s, int e)
    {
        int pivot = A[s];

        int i = s;
        int j = e + 1;
        while(i < j)
        {
            while(i<e && A[i+1] < pivot) i++;
            while(j > s && A[j] >= pivot) j--;

            if(i<j)
                swap(A[i+1], A[j]);
        }
