    int findLongestConsecutive(int n) {
        vector<int> nums;
        for(int i = 0; i < n; i++) {
            if(i == 0)
                nums.push_back(1);
            else {
                if(nums.back() < i)
                    nums.push_back(i);
                else
                    nums.push_back(nums.back() + 1);
            }
        }
