class Solution {
public:
    int ans=INT_MAX;
    void findAns(vector<vector<int>>v,int x,int y,int c0,int c1,int c2,int c3,int cnt,vector<vector<int>> vis) {
        if(x==v.size()) {
            ans=min(ans,c0);
            ans=min(ans,c1);
            ans=min(ans,c2);
            ans=min(ans,c3);
            return;
        }
        if(y==v[0].size()) {
            return findAns(v,x+1,0,c0,c1,c2,c3,cnt+1,vis);
        }
        if(vis[x][y]==1) {
            return findAns(v,x,y+1,c0,c1,c2,c3,cnt,vis);
        }

        findAns(v,x,y+1,c0+v[x][y],c1,c2,c3,cnt,vis);
    }

    void fill(vector<vector<int>>&vis,vector<vector<int>>& v) {
        int n=v.size();
        int i=0,j=0;
        while(i<=n/2) {
            vis[i][j]=1;
            i++;
            j++;
        }
        i=0,j=n-1;
        while(i<n/2) {
            vis[i][j]=1;
            i++;
            j--;
        }
        i=n/2+1;j=n/2;
        while(i<n) {
            vis[i][j]=1;
            i++;
        }
    }
    int minimumOperationsToWriteY(vector<vector<int>>& v) {
        int n=v.size();
        fill(vis,v);
        return findAns(v,0,0,0,0,0,0,0,vis);
    }
}; 

