using namespace std;

class TreeNode {
public:
	int val;
	TreeNode* left;
	TreeNode* right;
	TreeNode(int x) : val(x), left(NULL), right(NULL) {}
};

class Solution {
public:
	int kthSmallest(TreeNode* root, int k) {
		vector<int> treeVals;
		queue<TreeNode*> q;
		TreeNode* node = root;

		while (node || q.size()!= 0) {
			if (node) {
				q.push(node);
				node = node->left;
			}
			else {
				node = q.front();
				treeVals.push_back(node->val);
				q.pop();
				node = node->right;
			}
		}
		return treeVals[k - 1];
	}
};

