package LeetCode.TopInterviewQuestions.TwoPointers;

public class ReverseString {
    //Iterative
    //Time: O(n)
    //Space: O(1)
    public void reverseString(char[] s) {
        int n = s.length;
        for (int start = 0, end = n-1; start <= end; start++, end--) {
            char tmp = s[start];
            s[start] = s[end];
            s[end] = tmp;
        }
    }

    //Recursive
    //Time: O(n)
    //Space: O(n)
    public void reverseString(char[] s, int start, int end) {
        if (start >= end) return;
        char temp = s[start];
        s[start] = s[end];
        s[end] = temp;
        reverseString(s, start + 1, end - 1);
    }

    public static void main(String[] args) {
        ReverseString rs = new ReverseString();
        char[] input = {'h','e','l','l','o'};
        char[] input2 = {'h','e','l','l','o'};
        rs.reverseString(input);
        rs.reverseString(input2,0,input2.length-1);
        for(char c : input)
            System.out.print(c+" ");
        System.out.println();
        for(char c : input2)
            System.out.print(c+" ");
        System.out.println();
    }
}
