class Solution {
   public:
    int minimumCost(vector<int>& nums) {
        if (nums.size() < 3) return 0;
        int first = nums[0];
        int min1 = INT_MAX, min2 = INT_MAX;
        for (int i = 1; i < nums.size(); i++) {
            if (nums[i] < min1) {
                min2 = min1;
                min1 = nums[i];
            } else if (nums[i] < min2) {
                min2 = nums[i];
            }
        }
        return first + min1 + min2;
    }
};

