// Example 1
#include <iostream>
#include <vector>

using namespace std;

class Solution {
   public:
    int findMin(vector<int>& nums) {
        if (nums.size() == 0) {
            return -1;
        }
        int left = 0, right = nums.size() - 1;
        int result = INT_MAX;
        while (left <= right) {
            int mid = left + (right - left) / 2;
            if (nums[mid] < result) {
                result = nums[mid];
                left = mid + 1;
            } else {
                right = mid - 1;
            }
        }
        return result;
    }
};

int main() {
    Solution a;
    vector<int> nums;
    nums.push_back(5);
    nums.push_back(4);
    nums.push_back(3);
    int ans = a.findMin(nums);
    cout << ans << endl;
}
