import time
import sys

def LCS(sequence1,sequence2):
    #Initializing an LCS matrix
    LCS = [[0 for y in range(len(sequence2)+1)] for x in range(len(sequence1)+1)]
    #Iterating to fill the table
    for i in range(len(sequence1)):
        for j in range(len(sequence2)):
            if sequence1[i]==sequence2[j]:
                LCS[i+1][j+1] = LCS[i][j]+1
            else:
                LCS[i+1][j+1] = max(LCS[i][j+1],LCS[i+1][j])

    seq=[]
    LCS_length=LCS[len(sequences1)][len(sequences2)]

    for i in range(1, LCM(len(sequences1),len(sequences2))+1):
        if LCS[i][LCS_length-1]==LCS[i-1][LCS_length-1]:
            seq.append(i)
    return seq

def LCM(a, b):
    return abs(a*b) // gcd(a, b)

def gcd(a, b):
    while b:
        a, b = b, a % b
    return a

def LCSsequenceDP(sequence1, sequence2):
    n = len(sequence1)
    m = len(sequence2)
    dp = [[0 for _ in range(m+1)] for _ in range(n+1)]

    # Initialize the first row and column of the table
    for i in range(n+1):
        for j in range(m+1):
            if i == 0 or j == 0:
                dp[i][j] = 0
            elif sequence1[i-1] == sequence2[j-1]:
                dp[i][j] = dp[i-1][j-1] + 1
            else:
                dp[i][j] = max(dp[i-1][j], dp[i][j-1])

    # Find the LCS
    i, j = n, m
    while i > 0 and j > 0:
        if sequence1[i-1] == sequence2[j-1]:
            i -= 1
            j -= 1
        elif dp[i-1][j] > dp[i][j-1]:
            i -= 1
        else:
            j -= 1

    # Return the LCS sequence
    return dp[n][m]

